import React from 'react'
import { ANIMALS } from 'petfinder-client'
import { Consumer } from './searchContext'

// return (
//   <div className='search-params'>
//     <label htmlFor='region'>Region
//       <input onChange={searchParams.handleRegionChange} id='region' value={searchParams.region} placeholder='region' />
//     </label>

//     <label htmlFor='race'>Race
//       <select onChange={searchParams.handleRaceChange} onBlur={searchParams.handleRaceChange} id='race' value={searchParams.race} placeholder='race' >
//         <option />
//         {ANIMALS.map(animal => (<option key={animal} value={animal}>{animal}</option>))}
//       </select>
//     </label>

//     <label htmlFor='breed'>breed
//       <select onChange={searchParams.handleBreedChange} onBlur={searchParams.handleBreedChange} id='breed' value={searchParams.breed} placeholder='breed' disabled={searchParams.breeds.length === 0}>
//         <option />
//         {searchParams.breeds.map(breed => (<option key={breed} value={breed}>{breed}</option>))}
//       </select>
//     </label>

//     <button>Submit</button>
//   </div>
// )
export class SearchBox extends React.Component {
  render () {
    return (
      <Consumer>
        {context => (
          <div className='search-params'>
            <form onSubmit={this.props.search}>
              <label htmlFor='region'>Region</label>
              <label htmlFor='race'>Race</label>
              <label htmlFor='breed'>Breed</label>
            </form>
          </div>
        )}
      </Consumer>
    )
  }
}
